<?php/** * Склонение по числам */namespace Otus\Lessons\Lesson4;class DeclinerNumbers{    /**     * @param $n int     * @param $form1 string     * @param $form2 string     * @param $form5 string     * @return string     */    public function declinNumber($n, $form1, $form2, $form5): string    {        $n = abs($n) % 100;        $n1 = $n % 10;        if($n > 10 && $n < 20){            return $form5;        }        if($n1 > 1 && $n1 < 5){            return $form2;        }        if($n1 === 1){            return $form1;        }        return $form5;    }}